import { useState, useCallback } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import './SoundItemForm.css'

const SoundItemForm = (props) => {
    const [author, setAuthor] = useState('');
    const [song, setSong] = useState('');
    const [mp3, setMp3] = useState('');
    const [status, setStatus] = useState('stop');
    const handleSubmit = useCallback((event) => {
        event.preventDefault();
        setStatus('stop');
        props.onSave(author, song, mp3, status);
    }, [author, song, mp3, status, props]);

    const visibleForm = () => {
        props.onVisible(false);
    };
    return (
        <form className="sound-item-form" onSubmit={handleSubmit}>
            <div className="row mt-2">
                <div className="col-2   text-left">
                    <label className="form-label" htmlFor="author">Author</label>
                </div>
                <div className="col-10   text-right">
                    <input
                        className="form-control"
                        type="text"
                        name="author"
                        id="author"
                        required
                        onChange={(event) => setAuthor(event.target.value)}
                    />
                </div>
            </div>
            <div className="row mt-2">
                <div className="col-2   text-left">
                    <label className="form-label" htmlFor="song">Song</label>
                </div>
                <div className="col-10   text-right">
                    <input
                        className="form-control"
                        type="text"
                        name="song"
                        id="song"
                        required
                        onChange={(event) => setSong(event.target.value)}
                    />
                </div>
            </div>
            <div className="row mt-2">
                <div className="col-2   text-left">
                    <label className="form-label" htmlFor="mp3">Mp3</label>
                </div>
                <div className="col-10   text-right">
                    <input
                        className="form-control"
                        type="file"
                        name="mp3"
                        id="mp3"
                        accept="audio/*"
                        required
                        onChange={(event) => setMp3(event.target.value)}
                    />
                </div>
            </div>
            {/* <div className="row mt-2">
                <div className="col-2   text-left">
                    <label className="form-label" htmlFor="mp3">File</label>
                </div>
                <div className="col-10">
                    <input
                        type="file"
                        accept="audio/*"
                        required
                    /> */}
            {/* <Form.File
                        className="position-relative"
                        required
                        name="file"
                        accept=".mp3"
                        // label="File"
                        // onChange={handleChange}
                        // isInvalid={!!errors.file}
                        // feedback={errors.file}
                        id="validationFormik107"
                        feedbackTooltip
                    // onChange={(event) => setMp3(event.target.value)}
                    /> */}
            {/* </div> */}
            {/* </div > */}
            <div className="text-center">
                <Button type="submit" variant="outline-info mb-2 mt-3">Add 🎶</Button>
                <Button type="button" variant="outline-info mb-2 mt-3 ml-5" onClick={visibleForm}>Cancel 🎶</Button>
            </div>
        </form >
    );
}

export default SoundItemForm;
