import { useCallback } from 'react'
import './Playlist.css'

function Playlist(props) {
    const handleStatusChange = useCallback(() => {
        props.onChange(props.id, props.author, props.song, props.mp3, props.status);
        console.log(props.id);
    }, [props.id, props.status])
    return (
        <div className="song-item">
            { <div className={`song-item-status is-${props.status}`}>
                {props.status} {props.id}
            </div>}
            <div className="song-item-name">
                <span className="song-author">
                    {props.author}
                </span>
                <span className="song-title">
                    {props.song}
                </span>
            </div>
            <button onClick={handleStatusChange}>{(props.status === "stop") ? "play" : "stop"}</button>
        </div>
    )
}

export default Playlist;
